import {
    jumbotronStyle,
    widthStyle, heightStyle,
    centerStyle, leftStyle, rightStyle,
    fontSizeStyle, fontWeightStyle, fontStyle,
    paddingStyle, paddingLeftStyle, paddingRightStyle, paddingTopStyle, paddingBottomStyle,
    marginStyle, marginLeftStyle, marginRightStyle, marginTopStyle, marginBottomStyle
} from "./functions";

// Jumbotron
const jumbo = document.querySelectorAll(".jumbotron");
// Size (Width & Height)
const width = document.querySelectorAll('[class*="w-"]');
const height = document.querySelectorAll('[class*="h-"]');
// Fonts
const fontSize = document.querySelectorAll('[class*="fs-"]');
const fontWeight = document.querySelectorAll('[class*="fw-"]');
const fontType = document.querySelectorAll('[class*="ft-"]');
// Justify Content
const left = document.querySelectorAll(".left");
const right = document.querySelectorAll(".right");
const center = document.querySelectorAll(".center");
// Padding
const padding = document.querySelectorAll('[class*="p-"]');
const paddingLeft = document.querySelectorAll('[class*="pl-"]');
const paddingRight = document.querySelectorAll('[class*="pr-"]');
const paddingTop = document.querySelectorAll('[class*="pt-"]');
const paddingBottom = document.querySelectorAll('[class*="pb-"]');
// Margin
const margin = document.querySelectorAll('[class*="m-"]');
const marginLeft = document.querySelectorAll('[class*="ml-"]');
const marginRight = document.querySelectorAll('[class*="mr-"]');
const marginTop = document.querySelectorAll('[class*="mt-"]');
const marginBottom = document.querySelectorAll('[class*="mb-"]');
// Button
const button = document.querySelectorAll('[class*="btn-"]');

for (let element of jumbo) jumbotronStyle(element);
for (let element of width) widthStyle(element);
for (let element of height) heightStyle(element);
for (let { style } of center) centerStyle(style);
for (let element of left) leftStyle(element);
for (let element of right) rightStyle(element);
for (let element of fontSize) fontSizeStyle(element);
for (let element of fontWeight) fontWeightStyle(element);
for (let element of fontType) fontStyle(element);
for (let element of padding) paddingStyle(element);
for (let element of paddingLeft) paddingLeftStyle(element);
for (let element of paddingRight) paddingRightStyle(element);
for (let element of paddingTop) paddingTopStyle(element);
for (let element of paddingBottom) paddingBottomStyle(element);
for (let element of margin) marginStyle(element);
for (let element of marginLeft) marginLeftStyle(element);
for (let element of marginRight) marginRightStyle(element);
for (let element of marginTop) marginTopStyle(element);
for (let element of marginBottom) marginBottomStyle(element);
